import React, { useState } from "react";

function PostProperty() {
    const [propertyType, setPropertyType] = useState("");
    const [subType, setSubType] = useState("");
    const [city, setCity] = useState("");
    const [purpose, setPurpose] = useState("");
    const [dynamicFields, setDynamicFields] = useState("");
    const [description, setDescription] = useState("");
    const [ownerContactNo, setOwnerContactNo] = useState("");
    const [ownerContactEmail, setOwnerContactEmail] = useState("");
    const [price, setPrice] = useState("");
    const [formData, setFormData] = useState({
        propertyTypeId: "",
        propertySubTypeId: "",
        cityId: "",
        purpose: "",
        price: "",
        description: "",
        ownerContactNo: "",
        ownerContactEmail: "",
        imageUrls: "",
        // Dynamic fields will be added based on property type
    });

    const subTypes = {
        "1": ["Apartment/Flat", "Independent House/Villa"],
        "2": ["Office Space", "Retail/Shop Space", "Warehouse/Godown"],
        "3": ["Residential Land", "Agricultural Land", "Commercial Land", "Industrial Plot"],
        "4": ["Hostels", "PG", "Hotels/Resorts", "Apartment/Flat", "Independent House/Villa"]
    };

    const cities = [
        "Ahmedabad", "Surat", "Vadodara", "Rajkot", "Gandhinagar", "Bhavnagar", "Jamnagar",
        "Junagadh", "Anand", "Nadiad", "Mehsana", "Morbi", "Surendranagar", "Valsad",
        "Bharuch", "Navsari", "Dahod", "Patan", "Palanpur", "Porbandar"
    ];

    const dynamicFieldsData = {
        "1": (<>
            <div className="mb-3"><label className="form-label">BHK</label><input type="number" className="form-control" /></div>
            <div className="mb-3"><label className="form-label">Bathrooms</label><input type="number" className="form-control" /></div>
            <div className="mb-3"><label className="form-label">Balconies</label><input type="number" className="form-control" /></div>
            <div className="mb-3"><label className="form-label">Furnishing Status</label><input type="text" className="form-control" /></div>
            <div className="mb-3"><label className="form-label">Parking Available</label><select className="form-select"><option value="true">Yes</option><option value="false">No</option></select></div>
        </>),
        "2": (<div className="mb-3"><label className="form-label">Carpet Area (sq ft)</label><input type="number" className="form-control" /></div>),
        "3": (<>
            <div className="mb-3"><label className="form-label">Plot Area (sq ft)</label><input type="number" className="form-control" /></div>
            <div className="mb-3"><label className="form-label">Zoning Type</label><input type="text" className="form-control" /></div>
            <div className="mb-3"><label className="form-label">Road Access</label><select className="form-select"><option value="true">Yes</option><option value="false">No</option></select></div>
        </>),
        "4": (<>
            <div className="mb-3"><label className="form-label">Total Beds</label><input type="number" className="form-control" /></div>
            <div className="mb-3"><label className="form-label">Services</label><input type="text" className="form-control" /></div>
        </>)
    };

    // Property type mapping
    const propertyTypes = {
        1: "Residential",
        2: "Commercial",
        3: "Land",
        4: "Rental"
    };

    // Property sub-type mapping with IDs
    const subTypesWithIds = {
        1: [
            { id: 1, name: "Apartment/Flat" },
            { id: 2, name: "Independent House/Villa" }
        ],
        2: [
            { id: 3, name: "Office Space" },
            { id: 4, name: "Retail/Shop Space" },
            { id: 5, name: "Warehouse/Godown" }
        ],
        3: [
            { id: 6, name: "Residential Land" },
            { id: 7, name: "Agricultural Land" },
            { id: 8, name: "Commercial Land" },
            { id: 9, name: "Industrial Plot" }
        ],
        4: [
            { id: 10, name: "Hostels" },
            { id: 11, name: "PG" },
            { id: 12, name: "Hotels/Resorts" },
            { id: 13, name: "Apartment/Flat" },
            { id: 14, name: "Independent House/Villa" }
        ]
    };

    // Cities mapping with IDs
    const citiesWithIds = [
        { id: 1, name: "Ahmedabad" },
        { id: 2, name: "Surat" },
        { id: 3, name: "Vadodara" },
        // ... add all cities with their IDs
    ];

    const handlePropertyTypeChange = (e) => {
        const typeId = e.target.value;
        setPropertyType(typeId);
        setSubType("");
        setDynamicFields("");
        setFormData(prev => ({
            ...prev,
            propertyTypeId: typeId,
            propertySubTypeId: ""
        }));
    };

    const handleSubTypeChange = (e) => {
        const subTypeId = e.target.value;
        setSubType(subTypeId);
        setDynamicFields(dynamicFieldsData[propertyType] || "");
        setFormData(prev => ({
            ...prev,
            propertySubTypeId: subTypeId
        }));
    };

    const handleImageUpload = async (e) => {
        const files = Array.from(e.target.files);
        const uploadPromises = files.map(async (file) => {
            const formData = new FormData();
            formData.append('file', file);
            
            try {
                const response = await fetch('YOUR_CLOUD_UPLOAD_URL', {
                    method: 'POST',
                    body: formData
                });
                const data = await response.json();
                return data.url; // Assuming the response contains the URL
            } catch (error) {
                console.error('Error uploading image:', error);
                return null;
            }
        });

        const urls = (await Promise.all(uploadPromises)).filter(url => url);
        setFormData(prev => ({
            ...prev,
            imageUrls: urls.join(',')
        }));
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    return (
        <div className="container py-5">
            <div className="row justify-content-center">
                <div className="col-12 col-lg-8">
                    <div className="card border-0 shadow-lg">
                        <div className="card-body p-4 p-md-5">
                            <h2 className="text-center mb-4 text-primary fw-bold">Add Property</h2>
                            <form id="propertyForm">
                                {/* Basic Property Information */}
                                <div className="bg-light p-4 rounded-3 mb-4">
                                    <h5 className="mb-4 text-secondary fw-bold">
                                        <i className="bi bi-house-door me-2"></i>Basic Details
                                    </h5>
                                    <div className="row g-3">
                                        <div className="col-md-6">
                                            <label className="form-label fw-semibold">Property Type</label>
                                            <select 
                                                className="form-select shadow-sm" 
                                                value={formData.propertyTypeId}
                                                onChange={handlePropertyTypeChange}
                                                required
                                            >
                                                <option value="">Select Property Type</option>
                                                {Object.entries(propertyTypes).map(([id, name]) => (
                                                    <option key={id} value={id}>{name}</option>
                                                ))}
                                            </select>
                                        </div>
                                        <div className="col-md-6">
                                            <label className="form-label fw-semibold">Property Sub-Type</label>
                                            <select 
                                                className="form-select shadow-sm"
                                                value={formData.propertySubTypeId}
                                                onChange={handleSubTypeChange}
                                                required
                                            >
                                                <option value="">Select Sub-Type</option>
                                                {subTypesWithIds[formData.propertyTypeId]?.map(subType => (
                                                    <option key={subType.id} value={subType.id}>
                                                        {subType.name}
                                                    </option>
                                                ))}
                                            </select>
                                        </div>
                                        <div className="col-md-6">
                                            <label className="form-label fw-semibold">Purpose</label>
                                            <select className="form-select shadow-sm" value={purpose} onChange={(e) => setPurpose(e.target.value)} required>
                                                <option value="">Select Purpose</option>
                                                <option value="Sale">Sale</option>
                                                <option value="Rent">Rent</option>
                                            </select>
                                        </div>
                                        <div className="col-md-6">
                                            <label className="form-label fw-semibold">Price</label>
                                            <input 
                                                type="number" 
                                                className="form-control shadow-sm"
                                                name="price"
                                                value={formData.price}
                                                onChange={handleInputChange}
                                                placeholder="Enter property price"
                                                required
                                            />
                                        </div>
                                    </div>
                                </div>

                                {/* Location Information */}
                                <div className="bg-light p-4 rounded-3 mb-4">
                                    <h5 className="mb-4 text-secondary fw-bold">
                                        <i className="bi bi-geo-alt me-2"></i>Location Details
                                    </h5>
                                    <div className="row g-3">
                                        <div className="col-md-6">
                                            <label className="form-label fw-semibold">City</label>
                                            <select 
                                                className="form-select shadow-sm"
                                                name="cityId"
                                                value={formData.cityId}
                                                onChange={handleInputChange}
                                                required
                                            >
                                                <option value="">Select City</option>
                                                {citiesWithIds.map(city => (
                                                    <option key={city.id} value={city.id}>
                                                        {city.name}
                                                    </option>
                                                ))}
                                            </select>
                                        </div>
                                    </div>
                                </div>

                                {/* Property Specific Details */}
                                <div className="bg-light p-4 rounded-3 mb-4">
                                    <h5 className="mb-4 text-secondary fw-bold">
                                        <i className="bi bi-list-check me-2"></i>Property Specifications
                                    </h5>
                                    <div className="row g-3">
                                        {dynamicFields}
                                    </div>
                                </div>

                                {/* Property Description */}
                                <div className="bg-light p-4 rounded-3 mb-4">
                                    <h5 className="mb-4 text-secondary fw-bold">
                                        <i className="bi bi-file-text me-2"></i>Property Description
                                    </h5>
                                    <div className="mb-3">
                                        <label className="form-label fw-semibold">Description</label>
                                        <textarea 
                                            className="form-control shadow-sm"
                                            name="description"
                                            value={formData.description}
                                            onChange={handleInputChange}
                                            rows="4"
                                            placeholder="Enter detailed property description including features, amenities, and highlights"
                                            required
                                        />
                                    </div>
                                </div>

                                {/* Media Upload */}
                                <div className="bg-light p-4 rounded-3 mb-4">
                                    <h5 className="mb-4 text-secondary fw-bold">
                                        <i className="bi bi-images me-2"></i>Property Images
                                    </h5>
                                    <div className="mb-3">
                                        <label className="form-label fw-semibold">Upload Images</label>
                                        <input 
                                            type="file" 
                                            className="form-control shadow-sm" 
                                            onChange={handleImageUpload}
                                            multiple 
                                        />
                                        <div className="form-text mt-2">
                                            <i className="bi bi-info-circle me-1"></i>
                                            You can upload multiple images (Maximum 10 images)
                                        </div>
                                    </div>
                                </div>

                                {/* Contact Information */}
                                <div className="bg-light p-4 rounded-3 mb-4">
                                    <h5 className="mb-4 text-secondary fw-bold">
                                        <i className="bi bi-person-lines-fill me-2"></i>Contact Information
                                    </h5>
                                    <div className="row g-3">
                                        <div className="col-md-6">
                                            <label className="form-label fw-semibold">Owner Contact Number</label>
                                            <input 
                                                type="tel" 
                                                className="form-control shadow-sm"
                                                name="ownerContactNo"
                                                value={formData.ownerContactNo}
                                                onChange={handleInputChange}
                                                placeholder="Enter contact number"
                                                required
                                            />
                                        </div>
                                        <div className="col-md-6">
                                            <label className="form-label fw-semibold">Owner Contact Email</label>
                                            <input 
                                                type="email" 
                                                className="form-control shadow-sm"
                                                name="ownerContactEmail"
                                                value={formData.ownerContactEmail}
                                                onChange={handleInputChange}
                                                placeholder="Enter email address"
                                                required
                                            />
                                        </div>
                                    </div>
                                </div>

                                <button type="submit" className="btn btn-primary btn-lg w-100 fw-bold">
                                    <i className="bi bi-check2-circle me-2"></i>Submit Property
                                </button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default PostProperty;
