import React, { useState, useEffect } from "react";
function Properties() {
    const [selectedBuyRent, setSelectedBuyRent] = useState("Buy");
    const [selectedPropertyType, setSelectedPropertyType] = useState("");
    const [selectedSubType, setSelectedSubType] = useState("");
    const [selectedCity, setSelectedCity] = useState("All Cities");
    const [minPrice, setMinPrice] = useState("");
    const [maxPrice, setMaxPrice] = useState("");
    const [subTypes, setSubTypes] = useState([]);
    const [allProperties, setAllProperties] = useState([]);
    const [filteredData, setFilteredData] = useState([]);
    const [selectedBHK, setSelectedBHK] = useState("BHK");

    const propertyTypes = [
        { id: 1, name: "Residential" },
        { id: 2, name: "Commercial" },
        { id: 3, name: "Land" },
        { id: 4, name: "Rental" },
    ];

    const propertySubTypes = {
        Residential: ["Apartment/Flat", "Independent House/Villa"],
        Commercial: ["Office Space", "Retail/Shop Space", "Warehouse/Godown"],
        Land: ["Residential Land", "Agricultural Land", "Commercial Land", "Industrial Plot"],
        Rental: ["Hostels", "PG", "Hotels/Resorts", "Apartment/Flat", "Independent House/Villa"],
    };

    // Fetch all properties on component mount
    useEffect(() => {
        console.log("calle d ")
        const fetchProperties = async () => {
            try {
                const response = await fetch(
                    "https://localhost:7019/api/PropertiesApi/GetAllProperties"
                );

                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }

                const data = await response.json();
                console.log("Fetched data:", data);
                setAllProperties(data);
                //   setFilteredData(data); // Initialize filtered data
            } catch (error) {
                console.error("Error in fetch:", error);
            }
        };

        fetchProperties();
    }, []);

    // Handle Buy/Rent change
    const handleBuyRentChange = (event) => {
        const value = event.target.value;
        setSelectedBuyRent(value);
        setSelectedPropertyType("");
        setSelectedSubType("");
        setSubTypes(value === "Rent" ? propertySubTypes["Rental"] : []);
    };

    // Handle Property Type change
    const handlePropertyTypeChange = (event) => {
        const value = event.target.value;
        setSelectedPropertyType(value);
        setSubTypes(propertySubTypes[value] || []);
        setSelectedSubType(""); // Reset subtype on type change
    };



    // Filter logic
    useEffect(() => {
        const filterData = () => {
            let filtered = [...allProperties];


            // console.log("called Filter:", filtered);
            // Filter by Buy/Rent
            filtered = filtered.filter((property) =>
                selectedBuyRent === "Buy"
                    ? property.purpose === "Sale"
                    : property.purpose === "Rent"
            );

            // console.log(" after Filter by buy/rent:", filtered);

            // Filter by Property Type
            if (selectedPropertyType) {

                filtered = filtered.filter(
                    (property) =>
                        property.propertyTypeName == selectedPropertyType
                );
            }

            // console.log(" after Filter by Property Type:", filtered);

            // Filter by Sub Type
            if (selectedSubType) {
                filtered = filtered.filter(
                    (property) => property.subTypeName === selectedSubType
                );
            }

            // console.log(" after Filter by sub Type:", filtered);

            // Filter by City
            if (selectedCity !== "All Cities") {
                filtered = filtered.filter(
                    (property) => property.cityName === selectedCity
                );
            }

            // console.log(" after Filter by city:", filtered);

            // Filter by Min and Max Price
            if (minPrice) {
                filtered = filtered.filter(
                    (property) => parseFloat(property.price) >= parseFloat(minPrice)
                );
            }

            // console.log(" after Filter by min:", filtered);

            if (maxPrice) {
                filtered = filtered.filter(
                    (property) => parseFloat(property.price) <= parseFloat(maxPrice)
                );
            }

            // console.log(" after Filter by max:", filtered);

            if ((selectedBuyRent === "Buy" && selectedPropertyType == "Residential" && selectedBHK != "BHK")) {
                filtered = filtered.filter(
                    (property) => property.residentialDetails.bhk === selectedBHK
                );


            }

            console.log("filtered data", filtered)
            setFilteredData(filtered);

        };

        filterData();
        // setTimeout(console.log("Filter:", filteredData), 5000)

    }, [
        selectedBuyRent,
        selectedPropertyType,
        selectedSubType,
        selectedCity,
        minPrice,
        maxPrice,
        allProperties,
        selectedBHK
    ]);


    const generatePropertyCards = (filteredData) => {
        return filteredData.map((property) => {
            // Destructure common fields
            const {
                purpose,
                price,
                cityName,
                propertyTypeName,
                address,
                description,
                imageUrlList,
                residentialDetails,
                rentalDetails,
                commercialDetails,
                landDetails,
                subTypeName
            } = property;

            // Prepare type-specific details
            let bathrooms = null;
            let parkingAvailable = null;
            let area = null;
            let landarea = null;
            let bhk = null;
            let Furnishing = null;
            let balconies =null;
            let beds= null;
            let services=null;
            let zoningType=null;

            if (residentialDetails) {
                balconies=residentialDetails.balconies;
                Furnishing = residentialDetails.furnishingStatus;
                bhk = residentialDetails.bhk;
                bathrooms = residentialDetails.bathrooms;
                parkingAvailable = residentialDetails.parkingAvailable ? "Available" : "Not Available";
              
            } else if (commercialDetails) {
                area = `${commercialDetails.carpet_area} sq ft`;
            } else if (landDetails) {
                landarea = `${landDetails.plotArea} sq ft`;
                zoningType=landDetails.zoningType;
            } else if (rentalDetails) {
                beds=rentalDetails.totalBeds;
                services=rentalDetails.services;
                
            }

            return (
                <div className="col-12 col-md-6 col-xl-4" key={property.propertyId}>
                    <div className="single-featured-property mb-50">
                        <div className="property-thumb">
                            <img src={imageUrlList && imageUrlList[0] ? imageUrlList[0] : "img/bg-img/placeholder.jpg"} alt={address} />
                            <div className="tag">
                                <span>For {purpose}</span>
                            </div>
                            <div className="list-price">
                                <p>${price.toLocaleString()}</p>
                            </div>
                        </div>
                        <div className="property-content">
                            <h5>{propertyTypeName} Property , {subTypeName}</h5>
                            <p className="location">
                                <img src="img/icons/location.png" alt="Location" /> {address}, {cityName}
                            </p>
                            <p>{description}</p>
                            <div className="property-meta-data d-flex align-items-end justify-content-around">
                                {/* for residental */}
                                {bathrooms !== null && (
                                    <div className="bathroom d-flex flex-column justify-content-between align-items-center">
                                        <div>BHK</div>
                                        <div>{bhk}</div>
                                    </div>
                                )}
                                {bathrooms !== null && (
                                    <div className="bathroom d-flex flex-column justify-content-between align-items-center">
                                        <div>Furnishing</div>
                                        <div>{Furnishing}</div>
                                    </div>
                                )}
                                {parkingAvailable !== null && (
                                    <div className="bathroom d-flex flex-column justify-content-between align-items-center">
                                        <div>Parking</div>
                                        <div>{parkingAvailable}</div>
                                    </div>
                                )}
                                {balconies !== null && (
                                    <div className="bathroom d-flex flex-column justify-content-between align-items-center">
                                        <div>Balconies</div>
                                        <div>{balconies}</div>
                                    </div>
                                )}

                                {/* for rental */}

                                {beds !== null && (
                                    <div className="bathroom d-flex flex-column justify-content-between align-items-center">
                                        <div>Beds</div>
                                        <div>{beds}</div>
                                    </div>
                                )}
                                {services !== null && (
                                    <div className="bathroom d-flex flex-column justify-content-between align-items-center">
                                        <div>Services</div>
                                        <div>{services}</div>
                                    </div>
                                )}

                                {/* for Commercial */}
                                {area !== null && (
                                    <div className="bathroom d-flex flex-column justify-content-between align-items-center">
                                        <div>Area</div>
                                        <div>{area}</div>
                                    </div>
                                )}

                                {/* for land */}
                                {landarea !== null && (
                                    <div className="bathroom d-flex flex-column justify-content-between align-items-center">
                                        <div>Area</div>
                                        <div>{landarea}</div>
                                    </div>
                                )}
                                {zoningType !== null && (
                                    <div className="bathroom d-flex flex-column justify-content-between align-items-center">
                                        <div>ZoningType</div>
                                        <div>{zoningType}</div>
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>
                </div>
            );
        });
    };




    return (
        <>

            <section class="breadcumb-area bg-img" style={{ "background-image": "url(img/bg-img/hero1.jpg)" }}>
                <div class="container h-100">
                    <div class="row h-100 align-items-center">
                        <div class="col-12">
                            <div class="breadcumb-content">
                                <h3 class="breadcumb-title">Listings</h3>
                            </div>
                        </div>
                    </div>
                </div>
            </section>

            <div class="south-search-area">
                <div class="container">
                    <div class="row">
                        <div class="col-12">
                            <div class="advanced-search-form">
                                <div class="search-title">
                                    <p>Search for your home</p>
                                </div>
                                <form action="#" method="post" id="advanceSearch">
                                    <div class="row">

                                        {/* Buy/Rent Select */}
                                        <div className="col-12 col-md-4 col-lg-3">
                                            <div className="form-group">
                                                <select
                                                    className="form-control"
                                                    id="BuyRent"
